import re
import nltk
nltk.download('punkt')


def split_text_template(template, text):
    # Remove all whitespace characters
    template = re.sub(r"\s", "", template)
    text = re.sub(r"\s", "", text)
    
    # # Debugging output
    # print(f"Template after whitespace removal: {template}")
    # print(f"Text after whitespace removal: {text}")
    
    # Use nltk to split sentences more accurately
    template_sentences = re.split(r'([，,。;；])', template)
    text_sentences = re.split(r'([，,。;；])', text)
    
    # Clean and filter sentences
    template_sentences = [i.strip() for i in template_sentences if not re.fullmatch(r"[，,。;；]+", i) and i != ""]
    text_sentences = [i.strip() for i in text_sentences if not re.fullmatch(r"[，,。;；]+", i) and i != ""]
    
    # Debugging output
    print(f"*********Template sentences after splitting and cleaning: {template_sentences}")
    print(f"*********Text sentences after splitting and cleaning: {text_sentences}")
    
    # Compare the number of sentences
    if len(text_sentences) == len(template_sentences):
        return {"********text_sentences": text_sentences, "template_sentences": template_sentences}
    else:
        print("Number of sentences does not match:",len(template_sentences), len(text_sentences))
        return None


